pragma solidity ^0.4.18;

contract ERC20  {
    
  function totalSupply() public view returns (uint256);
   function transferfrom(address from,address to,uint256 value)internal;   
}

contract borrower is ERC20{
 
 string public name; 
 string public symbol; 
 uint256 public totalSupply; 
 uint256 value;
 
 address public borrower_address; 
 mapping (address => uint256)  balance; 
 function borrower() public{
     borrower_address=msg.sender;
     totalSupply = 10;
     balance[msg.sender] = totalSupply;
     name = "aa"; 
     symbol = "A1";
      
     } 
    
     function totalSupply() public view returns (uint256)
    {
        return totalSupply;
    }
    
  
    function transferfrom(address from,address to,uint256 _value)internal
    {
        balance[from] -= _value;
       balance[to] += _value;
      
    }
}


contract Financial is borrower {
    
    uint256 FI_interest;
    uint256 FI_duration;
    uint256 initial_ether;
    uint256 loan_count;
    address financial_institution;
    
    function Financial () public {
        financial_institution =msg.sender;
    }
    struct loandetails
    {
        address borrower;
        uint256 loanamount;
        uint256 token;
        uint256 duration;
        
    }
    mapping(address=> loandetails)public detail;
  //  mapping(uint256 => uint256)public balanceOf;
    
    function register(uint256 interest,uint256 duration)public payable
    {
        initial_ether = msg.value / 1 ether ;
        FI_interest=interest;
        FI_duration=duration;
    }
    function existing()public view returns(address,uint256,uint256,uint256)
    {
         return(financial_institution,initial_ether,FI_interest,FI_duration);
    }
    
    
    function borrowMoney(address _borrower,uint256 tokenvalue)public 
    {
        transferfrom(_borrower,financial_institution,tokenvalue);
        detail[_borrower].borrower = _borrower;
        detail[_borrower].token = tokenvalue;
        detail[_borrower].loanamount = (((tokenvalue * 1 ether)/100 * 80) ) / 1 ether;
        detail[_borrower].duration = 12;
        loan_count +=1;
        //borrower.transfer(detail[borrower].loanamount);
        //initial_ether -= detail[borrower].loanamount;
       
    }
   
    function transferther(address _borrower) public payable 
    {
        _borrower.transfer(msg.value);
        initial_ether -= msg.value;
    }
    
    function viewloanamount(address _borrower)public view returns(uint256)
    {
        return(detail[_borrower].loanamount);
        
    }
    
    }
